function get_talent_name($id)
{
  global $mmfpm_db;
  $sql = new SQL;
  $sql->connect($mmfpm_db['addr'], $mmfpm_db['user'], $mmfpm_db['pass'], $mmfpm_db['name']);
  $talent_name = $sql->fetch_row($sql->query("SELECT `spellname_loc0` FROM `dbc_spell` WHERE `spellID`={$id} LIMIT 1"));
  $sql->close();
  unset($sql);
  return $talent_name[0];
}


function get_talent_tab($id)
{
  global $mmfpm_db;
  $sql = new SQL;
  $sql->connect($mmfpm_db['addr'], $mmfpm_db['user'], $mmfpm_db['pass'], $mmfpm_db['name']);
  $talent_tab = $sql->fetch_row($sql->query("SELECT `tab` FROM `dbc_talent` WHERE `rank1`={$id} LIMIT 1"));
  $sql->close();
  unset($sql);
  return $talent_tab[0];
}


function get_talent_value($id)
{
  global $lang_id_tab;
  $talent_id = talent_id();

  if(isset($talent_id[$id][2]))
    return $talent_id[$id][2];
  else
    return 0;
}

